import React,{useState} from 'react';
// importamos las librerias de los iconos
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
// aca agregamos el icono que elegiremos
import { faCheckSquare,faEdit,faTimes } from '@fortawesome/free-solid-svg-icons';

// aca accedemos a tarea de ListaTarea que a la vez esta en app
const Tarea = ({ tarea }) => {
    const [editandoTarea,cambiarEditandoTareas] = useState(false);


    return (

        <li className='lista-tareas__tarea'>
            {/* llamamos la funcion de iconos*/}
            <FontAwesomeIcon
                //icon sera igual al icono que elegimos con anterioridad
                icon={faCheckSquare}
                // aca se le agrega el estilo css
                className='lista-tareas__icono lista-tareas__icono-check'
            />
            <div className='lista-tareas__texto'>
                {editandoTarea ? 
                    <form action='' className='formulario-editar-tarea'>
                        <input
                        type="text"
                        className='formulario-editar-tarea__input'
                        >
                            <button
                            type='submit'
                            className='formulario-editar-tarea__btn'
                            >Actualizar</button>
                        </input>
                    </form>
                    :
                    {}
                }
                
            </div>
            <div className='lista-tareas__contenedor-botones'>
            <FontAwesomeIcon
                icon={faEdit}
                className='lista-tareas__icono lista-tareas__icono-accion'
            />
             <FontAwesomeIcon
                icon={faTimes}
                className='lista-tareas__icono lista-tareas__icono-accion'
            />
            </div>

        </li>
    );
}

export default Tarea;
