import './App.css';
import FormularioTareas from './componentes/FormularioTareas';
import Header from './componentes/header';
import React,{useState} from 'react';
import ListaTareas from './componentes/ListaTareas';

const App=()=> {

  const [tareas, cambiarTareas] = useState(
    [
      {
        id:1,
        texto:'Lavar la ropa',
        completada:false
      },
      {
        id:2,
        texto:'Ir al gimnasio',
        completada:false
      }
    ]
  )
  // 
  const [mostrarCompletadas, cambiarMostarCompletadas] =useState();


  console.log(tareas);

  return (
    <div className='contenedor'>
      <Header/>
      <FormularioTareas tareas={tareas} cambiarTareas={cambiarTareas}/>
      {/* se le agrega la propiedad tareas para poder usar la lista de arriba en el componente listatareas */}
      <ListaTareas tareas={tareas} cambiarTareas={cambiarTareas}/>
    </div>
  );
}

export default App;
